import { Injectable } from '@angular/core';
import { Language } from 'data/language';
import { Resource } from 'data/resource';

@Injectable()
export class LanguagesService {

  languages: Language[] = [];
  i18n: any = [];

  loadI18n(i18nObj: any) {
    this.i18n = i18nObj;
  }

  getLanguages() {
    return [  { lang : 'en' , img : 'data:image/png;base64,'},
              { lang : 'cs' , img : 'data:image/png;base64,' },
              { lang : 'de' , img : 'data:image/png;base64,' },
              { lang : 'cz' , img : 'data:image/png;base64,'},
              { lang : 'es' , img : 'data:image/png;base64,'},
              { lang : 'fr' , img : 'data:image/png;base64,'},
              { lang : 'hu' , img : 'data:image/png;base64,'},
              { lang : 'it' , img : 'data:image/png;base64,'},
              { lang : 'jp' , img : 'data:image/png;base64,'},
              { lang : 'ja' , img : 'data:image/png;base64,'},
              { lang : 'ko' , img : 'data:image/png;base64,'},
              { lang : 'nl' , img : 'data:image/png;base64,'},
              { lang : 'pl' , img : 'data:image/png;base64,'},
              { lang : 'bg' , img : 'data:image/png;base64,'},
              { lang : 'pt' , img : 'data:image/png;base64,'},
              { lang : 'ru' , img : 'data:image/png;base64,'},
              { lang : 'tr' , img : 'data:image/png;base64,'},
              { lang : 'cn' , img : 'data:image/png;base64,'},
              { lang : 'tw' , img : 'data:image/png;base64,'} ];
  }

  saveLanguageAndResource(languageName, resourceName, resourceValue) {
      let languageExists = false;

      for(let language of this.languages) {
            if( language.name === languageName) {
                language.resources.push( new Resource({name : resourceName, value: resourceValue}));
                languageExists = true;
            }
      }
      if( !languageExists ){
        this.languages.push( Language.create({ name  : languageName, resources : new Array<Resource>(new Resource({name : resourceName , value : resourceValue}))}) );
      }
  }

  getLanguagesAndResources() {
    return this.languages;
  }

  // resourceKey = '@title1', lang = 'en' || undefined
  // this should return an object like this:
  // getResourceValue('@title1');
  // { "en": "This is my title", "ru": " "}
  // getResourceValue('@title1', 'en');
  // "This is my title";

  getResourceValue(resourceKey: string, lang?: string): string {
    let resourceValue = {};
    let langObj = '';
    if (!lang && this.i18n) {
      this.i18n.forEach(languageObj => {
        const resource = languageObj.resources.find(res => res.name === resourceKey);
        langObj = languageObj.name;
        resource && (resourceValue[languageObj.name] = resource.value);
      });
      return resourceValue[langObj];
    }
  }

  setResourceValue(resourceKey: string, resourceValue: string, lang?: string) {
    if (!lang) {
      const current_element = this.i18n[0].resources.find(elem => elem.name === resourceKey);
      if (current_element) {
        current_element.value = resourceValue;
      }
    }
  }
  setResourceName(resourceKey: string, name: string) {
    if (resourceKey) {
      const current_element = this.i18n[0].resources.find(elem => elem.name === resourceKey);
      if (current_element) {
        current_element.name = name;
      }
    }
  }
}
